class Wheel {
  constructor() {
    this.count = parseInt(
      document.querySelector(".page").getAttribute("data-count")
    );
    this.disabled = false;
    this.wheel = document.querySelector(".winwheel");
    this.modal = document.querySelector(".popup");
  }

  setCount() {
    const count = document.querySelector("._count");
    count.innerHTML = this.count;
  }

  spinFirst() {
    const button = document.querySelector(".wheel-btn");
    button.addEventListener("click", (e) => {
      if (!this.disabled && this.count == 2) {
        this.disabled = true;
        this.takeAway();
        this.wheel.classList.add("winwheel_bonus-2");
        setTimeout(() => {
          this.openModal(".popup-1");
        }, 5500);
      }
    });
  }

  spinSecond() {
    const buttonPopup = document.querySelectorAll(".btn-spin");
    for (let i = 0; i < buttonPopup.length; i++) {
      buttonPopup[i].addEventListener("click", (e) => {
        this.wheel.classList.remove("winwheel_bonus-2");
        this.modal.classList.remove("show");
        this.disabled = false;
        switch (this.count) {
          case 1:
            this.wheel.classList.add("winwheel_bonus-1");
            setTimeout(() => {
              this.openModal(".popup-2");
            }, 5500);
            break;

          case 0:
            break;
        }
        this.takeAway();
      });
    }
  }

  takeAway() {
    if (this.count > 0) this.count--;
    this.setCount();
  }

  openModal(selector) {
    document.querySelector(selector).classList.add("show");
  }

  init() {
    this.spinFirst();
    this.spinSecond();
  }
}

new Wheel().init();

